import * as d3 from 'd3'

const MARGIN = { TOP: 10, BOTTOM: 80, LEFT: 70, RIGHT: 10 }
const WIDTH = 500 - MARGIN.LEFT - MARGIN.RIGHT
const HEIGHT = 300 - MARGIN.TOP - MARGIN.BOTTOM

class D3Chart {
	constructor(element, data) {
		let vis = this

    vis.data = data;

		var lineColumn ='Breed';

		vis.g = d3.select(element)
			.append("svg")
				.attr("width", WIDTH + MARGIN.LEFT + MARGIN.RIGHT)
				.attr("height", HEIGHT + MARGIN.TOP + MARGIN.BOTTOM)
			.append("g")
				.attr("transform", `translate(${MARGIN.LEFT}, ${MARGIN.TOP})`)



    //need to clean up data.
		vis.data.forEach(d => {
	    d.Year = d.Month.substr(0,4)
			d.Month = d.Month.substr(5,2)
	  })
		vis.data.forEach(d => {
			d.NewDate = new Date(d.Year, d.Month, '01')
		})

		// unpivot the data
		var newArray = []
		vis.data.forEach(d => {
			newArray.push({Date: d.NewDate, Breed: 'West', Score: d.West,})
			newArray.push({Date: d.NewDate, Breed: 'Labrador', Score: d.Labrador})
		})


		var nested = d3.nest()



		console.log(vis.data)
		//console.log(newArray)

		// scales
		vis.x = d3.scaleTime().range([0, WIDTH])
		vis.y = d3.scaleLinear().range([HEIGHT, 0])


		// axis generators
		const xAxisCall = d3.axisBottom()
		const yAxisCall = d3.axisLeft()
			.ticks(6)
			.tickFormat(d3.format('.3s'))

		// axis groups
		const xAxis = vis.g.append("g")
			.attr("class", "x axis")
			.attr("transform", `translate(0, ${HEIGHT})`)
		const yAxis = vis.g.append("g")
			.attr("class", "y axis")


		vis.update(newArray)
	}

	update(data) {
		let vis = this

		console.log(data)


		vis.x.domain(d3.extent(data, d => d.date))



	}
}

export default D3Chart
