import React, { useState , useEffect, useRef } from 'react';


const Dropdown = ({ label, options, selected, onSelectedChange }) => {
  const [open, setOpen ] = useState(false);
  const ref = useRef();

  useEffect(() => {
    const onBodyClick = (event) => {
      if(ref.current.contains(event.target)) {
        //comes from UI form. Maybe a multi select wouldn't want to do this?
        return;
      };

      setOpen(false);
    };


    document.body.addEventListener("click", onBodyClick, { capture: true });

    return () => {
      document.body.removeEventListener("click", onBodyClick, {
        capture: true,
      });

    };
  }, []);

  const onHandleChange = (option) => {
    console.log(option)
    console.log(open)
    onSelectedChange(option)
  }

  const renderedOptions = options.map((option) => {
    console.log(option)
    var optionFound = 0
    console.log('baaaa')
    //console.log(selected)
    selected.map((sOption) => {
      console.log(sOption)
      if(option.value === sOption.value)
      optionFound =1
      console.log('heeeerrr')

    })

    if (optionFound === 1) {
    return null
    } else {
    return (
      <div
        key={option.value}
        className="item"
        onClick={() => onHandleChange(option)}
        >
        {option.label}
      </div>
    );

    }
  })

  const onHandleOpen = (event) => {

    if(event.target.className !== 'item') {
      setOpen(!open)
    }

  }

  return (
    <div ref={ref} className="ui form">
      <div className="field">
        <label className="label">{label}</label>
        <div
          onClick={(e) => onHandleOpen(e)}
          className={`ui selection dropdown ${open ? 'visible active': ''}`}
        >
          <i className="dropdown icon"></i>
          <div className="text">{selected[0].label}</div>
          <div
            onClick={() => setOpen(true)}
            className={`menu ${open ? 'visible transition': ''}`}
          >
          {renderedOptions}
        </div>
      </div>
    </div>
  </div>
  );
};

export default Dropdown;
