import React, { Component } from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { csv } from 'd3';
import data from './data/multiTimeline.csv';
import ChartWrapper from './components/ChartWrapper';
import Dropdown from './components/Dropdown';

const options = [
  {
    label: 'Retriever',
    value: 'Retriever',
  },
  {
    label: 'Westie',
    value: 'Westie',
  },
  {
    label: 'Dalmatian',
    value: 'Dalmatian',
  },
  {
    label: 'Labrador',
    value: 'Labrador',
  }
];

class App extends Component{

  state = {
    data: [],
    breed: [options[0]]
  }

  setBreed = (newbreed) => {
    let { breed } = this.state
    breed.push(newbreed);
    this.setState({ breed: breed })
  }

  componentDidMount() {
    csv(data)
      .then(data => this.setState({ data }))
      .catch(error => console.log(error));
  }
  renderChart() {
    if (this.state.data.length == 0) {
      return "No data yet"
    }
    console.log(this.state.data)
    console.log(this.state.breed)
    return <ChartWrapper data={this.state.data} breed={this.state.breed}/>
  }

  render() {

    return (
      <div>
        <Navbar bg="light">
          <Navbar.Brand>ReactforDataViz.NET</Navbar.Brand>
        </Navbar>
        <Container>
          <Row>
            <Col xs={12}>
            <Dropdown
              multiple
              label="Select a Breed"
              selected={this.state.breed}
              onSelectedChange={this.setBreed}
              options={options}
            />
            </Col>
          </Row>
          <Row>
            <Col md={12} xs={12}>{this.renderChart()}</Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
