import * as d3 from 'd3'


const MARGIN = { TOP: 20, BOTTOM: 70, LEFT: 70, RIGHT: 10 }
const WIDTH = 1000 - MARGIN.LEFT - MARGIN.RIGHT
const HEIGHT = 300 - MARGIN.TOP - MARGIN.BOTTOM

class D3Chart {
	constructor(element, data) {
		let vis = this

    vis.data = data;
		var xColumn = "Date"
		var yColumn = "Score"

		vis.g = d3.select(element)
			.append("svg")
				.attr("width", WIDTH + MARGIN.LEFT + MARGIN.RIGHT)
				.attr("height", HEIGHT + MARGIN.TOP + MARGIN.BOTTOM)
			.append("g")
				.attr("transform", `translate(${MARGIN.LEFT}, ${MARGIN.TOP})`)

		console.log(Object.keys(vis.data[0]).length) 

    //need to clean up data.
		vis.data.forEach(d => {
	    d.year = d.Month.substr(0,4)
			d.month = d.Month.substr(5,2)
			d.west = Number(d.West_Highland_White_Terrier)
			d.retriever = Number(d.Golden_Retriever)
			d.labrador = Number(d.Labrador_Retriever)
			d.dalmatian = Number(d.Dalmatian)
			// d.west = Number(d.West)
			// d.labrador = Number(d.Labrador)
	  })
		vis.data.forEach(d => {
			d.NewDate = new Date(d.year, d.month, '01')
		})

	console.log(Object.keys(vis.data[0]).length)
		// unpivot the data
		var newArray = []
		vis.data.forEach(d => {
			//needs to be refactored to be dynamic


			newArray.push({Date: d.NewDate, Breed: 'Westie', Score: d.west})
			newArray.push({Date: d.NewDate, Breed: 'Retriever', Score: d.retriever})
			newArray.push({Date: d.NewDate, Breed: 'Labrador', Score: d.labrador})
			newArray.push({Date: d.NewDate, Breed: 'Dalmatian', Score: d.dalmatian})
		})

		console.log(newArray)

		// axis groups
		vis.xAxis = vis.g.append("g")
			.attr("class", "x axis")
			.attr("transform", `translate(0, ${HEIGHT})`)
		vis.yAxis = vis.g.append("g")
			.attr("class", "y axis")

		// Path generator

		vis.update(newArray)
	}

	update(data) {
		let vis = this

		var lineColumn ='Breed';
		var xColumn = "Date";
		var yColumn = "Score";

		// scales
		const x = d3.scaleTime()
			.domain(d3.extent(data, d => d.Date))
			.range([0, WIDTH])

		const y = d3.scaleLinear()
			.domain([0, d3.max(data, d => d.Score)])
			.range([HEIGHT, 0])

		// generate axes once scales have been set
		vis.xAxisCall = d3.axisBottom()
		vis.xAxis.call(vis.xAxisCall.scale(x))

		vis.yAxisCall = d3.axisLeft()
			.ticks(6)
			.tickFormat(d3.format('.3s'))
		vis.yAxis.call(vis.yAxisCall.scale(y))

		var nested = Array.from(
		 d3.group(data, d => d[lineColumn]), ([key, value]) => ({key, value})
	 );
		console.log(nested)

		// set the colour scale
    var color = d3.scaleOrdinal(d3.schemeCategory10);

		// spacing for the legend
		var legendSpace = WIDTH/nested.length;

		//nested.map((i) => console.log(i.value));

		const paths = vis.g.selectAll('.line')
		.data(nested)

		paths.exit().remove()

		paths.enter().append("path")
			.attr("fill", "none")
			.attr("stroke", function(d){ return color(d.key) })
			.attr("stroke-width", 2)
			.attr("d", function(d){
        return d3.line()
          .x(function(d) { return x(d[xColumn]); })
          .y(function(d) { return y(d[yColumn]) })
          (d.value)
      })


		nested.forEach(function(d,i) {
			// Add the Legend
      vis.g.append("text")
          .attr("x", (legendSpace/2)+i*legendSpace)  // space legend
          .attr("y", HEIGHT + (MARGIN.BOTTOM/2)+ 5)
          .attr("class", "legend")    // style the legend
          .style("fill", function() { // Add the colours dynamically
              return d.color = color(d.key); })
          .text(d.key);
		})

	}
}

export default D3Chart
