import * as d3 from 'd3'


const MARGIN = { TOP: 10, BOTTOM: 80, LEFT: 70, RIGHT: 10 }
const WIDTH = 600 - MARGIN.LEFT - MARGIN.RIGHT
const HEIGHT = 300 - MARGIN.TOP - MARGIN.BOTTOM

class D3Chart {
	constructor(element, data) {
		let vis = this

    vis.data = data;
		var xColumn = "Date"
		var yColumn = "Score"



		vis.g = d3.select(element)
			.append("svg")
				.attr("width", WIDTH + MARGIN.LEFT + MARGIN.RIGHT)
				.attr("height", HEIGHT + MARGIN.TOP + MARGIN.BOTTOM)
			.append("g")
				.attr("transform", `translate(${MARGIN.LEFT}, ${MARGIN.TOP})`)



    //need to clean up data.
		vis.data.forEach(d => {
	    d.Year = d.Month.substr(0,4)
			d.Month = d.Month.substr(5,2)
			d.West = Number(d.West)
			d.Labrador = Number(d.Labrador)
	  })
		vis.data.forEach(d => {
			d.NewDate = new Date(d.Year, d.Month, '01')
		})

		// unpivot the data
		var newArray = []
		vis.data.forEach(d => {
			newArray.push({Date: d.NewDate, Breed: 'West', Score: d.West,})
			newArray.push({Date: d.NewDate, Breed: 'Labrador', Score: d.Labrador})
		})


		console.log(vis.data)
		//console.log(newArray)




		// axis groups
		vis.xAxis = vis.g.append("g")
			.attr("class", "x axis")
			.attr("transform", `translate(0, ${HEIGHT})`)
		vis.yAxis = vis.g.append("g")
			.attr("class", "y axis")

		// Path generator

		vis.update(newArray)
	}

	update(data) {
		let vis = this

		var lineColumn ='Breed';
		var xColumn = "Date";
		var yColumn = "Score";

		vis.x.domain(d3.extent(data, d => d.Date))
		vis.y.domain([0, d3.max(data, d => d.Score)])

	//gg

		// generate axes once scales have been set
		vis.xAxisCall = d3.axisBottom()
		vis.xAxis.call(vis.xAxisCall.scale(vis.x))

		vis.yAxisCall = d3.axisLeft()
			.ticks(6)
			.tickFormat(d3.format('.3s'))
		vis.yAxis.call(vis.yAxisCall.scale(vis.y))

		console.log(data)
		//var nested = Array.from(d3.group(data, d => d[lineColumn]),([key, value]) => ({key, value}));
		//var nested = d3.group(data, d => d[lineColumn])

		var nested = Array.from(
		 d3.group(data, d => d[lineColumn]), ([key, value]) => ({key, value})
	 );
		console.log(nested)

		// set the colour scale
    var color = d3.scaleOrdinal(d3.schemeCategory10);


		const line = d3.line()
    .x(function(d) { return vis.x(d[xColumn]); })
    .y(function(d) { return vis.y(d[yColumn]); });

		nested.forEach(function(d,i) {
			vis.g.append("path")
			.attr("class", "chart-line")
			.style("stroke", function() { // Add the colours dynamically
				return d.color = color(d.key);
			})
			.attr("d", line(d.value));

		})


	}
}

export default D3Chart
