import React, { Component } from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { csv } from 'd3';
import data from './data/multiTimeline.csv';
import ChartWrapper from './components/ChartWrapper';
import BreedDropdown from './components/BreedDropdown';

const options = [
  {
    label: 'Afrikaans',
    value: 'af',
  },
  {
    label: 'Arabic',
    value: 'ar',
  },
  {
    label: 'Hindi',
    value: 'hi',
  },
];

class App extends Component{

  state = {
    data: []
  }

  //breedSelected = (breed) => this.setState({ breed })

  componentDidMount() {
    csv(data)
      .then(data => this.setState({ data }))
      .catch(error => console.log(error));
  }
  renderChart() {
    if (this.state.data.length == 0) {
      return "No data yet"
    }
    console.log(this.state.data)
    return <ChartWrapper data={this.state.data} />
  }

  render() {
    // const [language, setLanguage] = useState(options[0]);
    // const [text, setText] = useState('');

    // <Dropdown
    //   label="Select a Language"
    //   selected={language}
    //   onSelectedChange={setLanguage}
    //   options={options}
    // />
    return (
      <div>
        <Navbar bg="light">
          <Navbar.Brand>ReactforDataViz.NET</Navbar.Brand>
        </Navbar>
        <Container>
          <Row>
            <Col xs={12}>

            </Col>
          </Row>
          <Row>
            <Col md={12} xs={12}>{this.renderChart()}</Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
